import * as React from "react"
import { View, ImageStyle, StyleProp, ViewStyle, TouchableOpacity, TextStyle } from "react-native"
import { Text } from "../text/text"

export interface NameChipProps {
  name: string
  color?: string
  size?: number
}

const chipStyle: ViewStyle = {
  width: 50,
  height: 50,
  borderRadius: 50,
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  // boxShadow: '0px 3px 2px rgba(0, 0, 0, 0.25)'
  shadowColor: "black",
  shadowOffset: { width: 0, height: 2 },
  shadowOpacity: 0.25,
  shadowRadius: 5,
}

const initialsStyle: TextStyle = {
  color: "white",
  fontSize: 25,
  // fontFamily: 'Inter',
  shadowColor: "blue",
  shadowOffset: { width: 4, height: 4 },
  shadowOpacity: 1,
}

export function NameChip(props: NameChipProps) {
  var randomColor = Math.floor(Math.random() * 16777215).toString(16);

  const { name, color = randomColor, size = 50 } = props

  return (
    <View>
      <TouchableOpacity style={{ ...chipStyle, width: size, height: size, backgroundColor: color, borderRadius: size }}>
        <Text style={{ ...initialsStyle, fontSize: size / 2 }}>JG</Text>
      </TouchableOpacity>
    </View >
  )
}
