import * as React from "react"
import { View, ImageStyle, StyleProp, ViewStyle, TouchableOpacity, TextStyle } from "react-native"
import { color, spacing } from "../../theme"
import { Text } from "../text/text"

export interface dividerProps {
    isVertical?: boolean
    size?: string
}

export function Divider(props: dividerProps) {

    const { isVertical = false, size = '100%' } = props

    return (
        <View style={{ marginHorizontal: spacing[4], width: isVertical ? '1px' : size, height: isVertical ? size : '1px', backgroundColor: color.black }} />

    )
}
